/** * @author Barret Lee(barret.china@gmail.com) * @date 2014/07 */ // 默认关闭 debug 模式 var debug = false; // 关闭 debug 后，这里的 log 就不能用了 var log = function(msg){ if(!debug) return; console && console.log(msg); }; // 选法的配置，x 选 x var config = { "totalNum": 11, "selectNum": 5 }; /** * @description Cathectic为投注实现的一个类 * */ var Cathectic = function(){ // 当前选球的个数 var curNum = 1; // 当前选球方式，默认为“定位” var chooseType = "dingwei"; /** * @description 构造 select 模板的自执行函数 * @param {Number} config['totalNum'] 选择的最大个数 * @return {String} 返回拼接好的字符串 */ var chooseTpl = function(N){ var str = [ '', '选择号码遗漏' ]; for(var i = 1; i = half && $(me).addClass("cathectic-selector-item_on"); }, "small": function(index, me){ index + 1 < half && $(me).addClass("cathectic-selector-item_on"); }, "odd": function(index, me){ (index + 1) % 2 == 0 && $(me).addClass("cathectic-selector-item_on"); }, "even": function(index, me){ (index + 1) % 2 == 1 && $(me).addClass("cathectic-selector-item_on"); }, "other": function(index, me){ $.inArray(index + 1, rnum) > -1 && $(me).addClass("cathectic-selector-item_on"); } }; var rnum, self = this; // 当 select 的值发生变化的时候，修改球的选中状态 $(".cathectic-selector-item select").off().on("change", function(){ var $this = $(this), val = $this.val(); var cb = /\d/.test(val) ? funcPool["other"] : funcPool[val]; var except = []; $(".cathectic-selector-item") .eq(0).find(".cathectic-selector-item_on") .map(function(index, me){ except.push(Number($(me).text())); }); var total = config['totalNum']; // 对胆拖投注的特殊处理，随机选择多个球 if(chooseType == "dantuo" && except.length > 0){ val = val > total - except.length ? total - except.length : val; rnum = Cathectic.util.getSomeRandom(val, except); } else { rnum = Cathectic.util.getSomeRandom(val); } $this.parents(".cathectic-selector-item") .find("span strong") .removeClass("cathectic-selector-item_on").map(cb); self._setMoney(); }); $(".cathectic-selector-item strong").off().on("click", function(){ var $obj = $(this).parents(".cathectic-selector-item"); // 胆拖的处理，控制球数 if(chooseType == "dantuo" && $obj.index() == 0 && !$(this).hasClass("cathectic-selector-item_on") && $obj.eq(0).find(".cathectic-selector-item_on").size() == curNum - 1 ){ self.alertBox("您好, 胆码的选择个数不能超过"+(curNum - 1)+"个！") return; } var text = $(this).text(); if(chooseType == "dantuo"){ $obj.siblings().find(".cathectic-selector-item_on").map(function(index, me){ $(me).text() == text && $(me).removeClass("cathectic-selector-item_on"); }); } $(this).toggleClass("cathectic-selector-item_on"); self._setMoney(); }); }, /** * @description 核心算法，计算注数 * @return {Number} 注数 */ _calcMoney: function(){ var res = []; $(".cathectic-selector-item").each(function(){ var arr = []; $(this).find(".cathectic-selector-item_on").map(function(index, me){ arr.push($(me).text()); }); res.push(arr); }); curRes = res; // 定位投注 if(chooseType == "dingwei"){ if(curNum == 1){ return res[0].length; } if(curNum == 2){ var a = res[0].length, b = res[1].length; if( a * b > 0){ return a * b - (a + b - $.unique(res[0].concat(res[1]).sort()).length); } } if(curNum == 3){ var a = res[0].length, b = res[1].length, c = res[2].length; var ab = a+b-$.unique(res[0].concat(res[1]).sort()).length; var ac = a+c-$.unique(res[0].concat(res[2]).sort()).length; var bc = b+c-$.unique(res[1].concat(res[2]).sort()).length; var abc = 0; var inter = Cathectic.util.arrayIntersection(res[0], res[1]); if(inter.length > 0){ abc = inter.length + c - $.unique(inter.concat(res[2]).sort()).length } // 只是改变[选好了]这个按钮的状态，这里做判断 if(a * b * c > 0 && $.unique(res[0].concat(res[1]).concat(res[2]).sort()).length >= 3 && !((a == b && b == abc || a == c && c == abc || b == c && c == abc) && abc == 1) || (a == b && a == c && b == c && abc >= 3)){ return a*b*c - ab*c - ac*b - bc*a + abc * 2; } } } // 胆拖 if(chooseType == "dantuo"){ if(res[0].length >= 1 && res[0].length + res[1].length > curNum){ return this.permutation(res[1].length, curNum - res[0].length); } } // 普通 if(chooseType == "normal"){ if(res[0].length < curNum) return; return this.permutation(res[0].length, curNum); } // 单式上传 if(chooseType == "text") { var tmp = []; $.map($(".cathectic-main-choose-text textarea").val().split(/
/m), function(val){ if(val != "") tmp.push(val); }); return tmp.length; } }, // 处理单式上传 _dealText: function(){ var val = $(".cathectic-main-choose-text textarea").val().split(/
/m), arrText = [], self = this; for(var i = 0; i < val.length; i++){ if($.trim(val[i]) == "") continue; var arrTmp = val[i].split(/\s+|,/); var hashTable = {"length": 0}; // 输入数值错误，数值区间应该是 1 - totalNum for(var j = 0; j < arrTmp.length; j++){ var valTmp = parseInt(arrTmp[j]); if(valTmp > config['totalNum'] || valTmp = selectNum + 1){ doubling = this.permutation(curRes[0].length - 5, curNum - 5); } $win.text(basic * doubling); } if(chooseType == "dingwei"){ $win.text(basic); } if(chooseType == "dantuo"){ var doubling = 1, len; len = $.unique(curRes[0].concat(curRes[1]).sort()).length; if(curNum >= selectNum + 1){ doubling = this.permutation(len - 5, curNum - 5); } $win.text(basic); } $get.text(parseInt($win.text().replace(",","")) - parseInt($money.text()) + ".00"); } }, // 重置 button 的状态，并修改文案（数字） resetBtn: function(){ $(".CSR_btn").removeClass("CSR_btn_OK"); $(".cathectic-selector-item_on").removeClass("cathectic-selector-item_on"); this.resetDOM(); }, /** * @description 点击选好了之后，下面的 box 会添加一个记录， * 本函数是用来创建一个模板 * @param [Object] 传过来需要记录的信息 * @return {String} 模板字符串 */ _getItemTpl: function(data){ var tpl = ['', '删除', '[]', '', '(注元)', ''].join(""); var content = " "; if(chooseType == "dantuo"){ content = " 胆："; content += curRes[0].join(","); content += " 拖："; content += curRes[1].join(","); } if(chooseType == "dingwei"){ var tmp = []; for(var i = 0, len = curRes.length; i < len; i++){ tmp.push(curRes[i].join(",")); } content += tmp.join("|") + Cathectic.util.repeat("|-", 5 - curNum); } if(chooseType == "normal" || chooseType == "text"){ content += curRes[0].join(","); } // 模板数据 var data = $.extend({ "num": $(".CSR_info span").eq(0).text(), "money": $(".CSR_info span").eq(1).text(), 'type': $("#" + curId).attr("data-type"), "content": content + " " }, data || {}); // 替换内容的模板 return tpl.replace(/]+)?%>/g, function(m, a){ return data[a]; }); // return Cathectic.util.tplEngine(tpl, data); }, // 一些数字文案的还原处理 resetDOM: function(){ if(chooseType == "dingwei" || chooseType == "normal"){ $(".CSR_select").show(); } else { $(".CSR_select").hide(); } $(".CSR_get span").eq(0).text("0"); $(".CSR_get span").eq(1).text("0.00"); $(".CSR_info span").eq(0).text("0"); $(".CSR_info span").eq(1).text("0.00"); $(".cathectic-main-choose-text textarea").val(""); }, // 计算排列数值 permutation: function(a, b){ var resX = 1, resY = 1, tmp = b; while(tmp){ resX *= a; a--;tmp--; } while(b){ resY *= b; b--; } return resX / resY; }, // 该函数可扩展，弹出提醒 alertBox: function(msg){ alert(msg); }, /** * @description 在选好了附近和下方有很多可以点击的按钮和链接， * 这个函数内部主要是对上述按钮、链接做绑定 */ bindResultBox: function(){ var self = this; $(".CSR_clear").on("click", function(evt){ evt.preventDefault(); $(".cathectic-selector-item_on").removeClass("cathectic-selector-item_on"); $(".CSR_get span").eq(0).text("0"); $(".CSR_get span").eq(1).text("0.00"); self._setMoney(); }); var keydownTimer = null $(".cathectic-main-choose-text textarea").on("keydown", function(){ clearTimeout(keydownTimer); setTimeout(function(){ self._setMoney(); }, 100); }); $(".cathectic-resluts-list").on("click", "a", function(evt){ evt.preventDefault(); $(this).parent().remove(); self._updateTotalMoney(); }); $(".cathectic-resluts-list").on("click", "div", function(evt){ evt.preventDefault(); $(this).addClass("cathectic-resluts-list_active") .siblings().removeClass("cathectic-resluts-list_active"); }); $(".cathectic-resluts-doubling a").on("click", function(evt){ evt.preventDefault(); $(".cathectic-resluts-list").html(""); self._updateTotalMoney(); }); $(".cathectic-resluts-doubling i").on("click", function(){ var index = $(this).index(); var $val = $("#doubling"); var val = Number($val.val()); if(index == 0){ $val.val( val - 1 > 0 ? val - 1 : 1); } else { $val.val(val + 1); } curDoubling = $val.val(); $(".cathectic-resluts-doubling input").trigger("change"); }); $(".cathectic-resluts-doubling input").on("change keydown blur", function(){ self._updateTotalMoney($(this).val()); }); }, // 更新注数和钱数 _updateTotalMoney: function(N){ var $num = $(".cathectic-resluts-doubling strong").eq(0); var $money = $(".cathectic-resluts-doubling strong").eq(1); var total = 0; $(".cathectic-resluts-list > div").map(function(index, me){ total += Number($(me).attr("data-num")); }); var N = N || curDoubling; $num.text(total); $money.text(total * (N || 1) * 2 + ".00"); }, // 去重函数 _checkIsUse: function(){ var err = [], a1, a2, a3; if(chooseType == "dingwei" && curNum == 3){ a1 = this._checkIsUseHelper(curRes[0], curRes[1], curRes[2]); a2 = this._checkIsUseHelper(curRes[1], curRes[2], curRes[0]); a3 = this._checkIsUseHelper(curRes[0], curRes[2], curRes[1]); err = err.concat(a1).concat(a2).concat(a3); } if(chooseType == "dingwei" && curNum == 2){ a1 = this._checkIsUseHelper(curRes[0], curRes[1]); a2 = this._checkIsUseHelper(curRes[1], curRes[0]); err = err.concat(a1).concat(a2); } return $.unique(err); }, // 辅助上述去重 _checkIsUseHelper: function(a, b, c){ var res = []; var inter = Cathectic.util.arrayIntersection(a, b); if(inter.length > 0){ for(var i = 0; i < inter.length; i++){ if(curNum == 3){ res.push([inter[i],inter[i], c.join(",")].join("|")); } else { res.push([inter[i],inter[i]].join("|")); } } } return res; }, // 错误处理 errorHandler: function(){ window.onerror = function(info){ if(/^script error.$/i.test(info)){ return true; } return true; } }, // 选择多注的 select 绑定 bindSelector: function(){ var self = this; $(".CSR_select").on("change", function(){ var val = +$(this).val(); if(val == 0) return; for(var s = 0; s < val; s++){ var tmp = []; curRes = []; if(chooseType == "dingwei"){ for(var i = 0; i < curNum; i++){ var ls = Cathectic.util.getRandom(tmp); tmp.concat(ls); curRes.push([ls]); } } if(chooseType == "normal"){ curRes.push(Cathectic.util.getSomeRandom(curNum)); } // console.log(curRes ,self._calcMoney()); var str = self._getItemTpl({ "num": 1, "money": "2.00", 'type': $("#" + curId).attr("data-type") }); $(".cathectic-resluts-list").append(str); } self._updateTotalMoney(); $(".cathectic-resluts-list").scrollTop($(".cathectic-resluts-list").children().size()*24); }); } } }(); /** * @description 对Cathectic类的扩展，添加辅助工具 * @method getRandom 获取随机数 * @method getSomeRandom 获取多个随机数 * @method tplEngine 模板引擎 * @method repeat 字符串重复 * @method arrayIntersection 数组交集 */ Cathectic.util = function(){ return { /** * @description 获取随机数 * @param [Array] except 剔除的数 * @return [Array] 返回一个数组 */ getRandom: function(except){ var total = config['totalNum']; if(!except || except.length == 0){ return Math.floor(Math.random()*total)+1; } var res, tag = true; while(1 && tag){ res = Math.floor(Math.random()*total)+1; ($.inArray(res, except) == -1) && (tag = false); } return res == total + 1 ? res - 1 : res; }, /** * @description 获取随机数 * @param [Number] num 个数 * @param [Array] except 获取多个随机数 * @return [Array] 返回一个数组 */ getSomeRandom: function(num, except){ var res = [], args; while(num--){ if(except && res.length == 0){ args = except; } else { args = $.unique((except || []).concat(res).sort()); } var tmp = this.getRandom(args); res.push(tmp); } return res.sort(function(a, b){return a > b}); }, // 模板引擎，详情可以异步我写的一篇博客 // http://www.cnblogs.com/hustskyking/p/principle-of-javascript-template.html tplEngine: function(tpl, data) { var reg = /]+)?%>/g, regOut = /(^( )?(if|for|else|switch|case|break|{|}))(.*)?/g, code = 'var r=[];
', cursor = 0; var add = function(line, js) { js? (code += line.match(regOut) ? line + '
' : 'r.push(' + line + ');
') : (code += line != '' ? 'r.push("' + line.replace(/"/g, '\"') + '");
' : ''); return add; } while(match = reg.exec(tpl)) { add(tpl.slice(cursor, match.index))(match[1], true); cursor = match.index + match[0].length; } add(tpl.substr(cursor, tpl.length - cursor)); code += 'return r.join("");'; return new Function(code.replace(/[	
]/g, '')).apply(data); }, // 简单易懂，不多说 repeat: function(str, N){ var res = "" while(N--){ res += str; } return res; }, /** * @description 数据去重 * @param {Array} a 要比较的数组 * @param {Array} b 要比较的数组 * @return {Array} a 和 b 的交集 */ arrayIntersection: function (a, b) { var ai = 0, bi = 0; var result = new Array(); while( ai < a.length && bi < b.length ){ if (a[ai] < b[bi] ){ ai++; } else if (a[ai] > b[bi] ){ bi++; } else { result.push(a[ai]); ai++; bi++; } } return result; } } }(); // 整个函数启动的入口，调用 Cathectic 的初始化函数。 $(function(){ Cathectic.init(); });

